    # –ü–µ—Ä–≤–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ –¥–ª—è content
    first_page_text = pages[0]["text"]

    # –í—Å–µ —Å—Ç—Ä–∞–Ω–∏—Ü—ã –≤ JSON
    pages_json = json.dumps(pages)

    print(f"\nüìù Updating: {button_id}")
    print(f"   ‚Ä¢ Total pages: {len(pages)}")
    print(f"   ‚Ä¢ First page length: {len(first_page_text)} chars")

    # –°–Ω–∞—á–∞–ª–∞ –ø–æ–ª—É—á–∞–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â—É—é –∑–∞–ø–∏—Å—å, —á—Ç–æ–±—ã –Ω–µ –ø–æ—Ç–µ—Ä—è—Ç—å –¥–∞–Ω–Ω—ã–µ
    from database import get_button_content, pool
    existing = None
    try:
        async with pool.acquire() as conn:
            existing = await conn.fetchrow(
                'SELECT buttons_json, parent_id, buttons_per_row FROM button_content WHERE button_id = $1',
                button_id
            )
    except Exception as e:
        print(f"   ‚ö†Ô∏è  Could not fetch existing data: {e}")

    # –ò—Å–ø–æ–ª—å–∑—É–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –∑–Ω–∞—á–µ–Ω–∏—è –∏–ª–∏ None
    existing_buttons_json = existing['buttons_json'] if existing else None
    existing_parent_id = existing['parent_id'] if existing else None
    existing_buttons_per_row = existing['buttons_per_row'] if existing else None

    print(f"   ‚Ä¢ Preserving: buttons_json={bool(existing_buttons_json)}, parent_id={existing_parent_id}")

    # –û–±–Ω–æ–≤–ª—è–µ–º —Ç–æ–ª—å–∫–æ content –∏ pages_json, —Å–æ—Ö—Ä–∞–Ω—è—è –æ—Å—Ç–∞–ª—å–Ω–æ–µ
    success = await update_button_content(
        button_id,
        first_page_text,
        None,  # photo_file_id
        existing_buttons_json,  # –°–æ—Ö—Ä–∞–Ω—è–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –∫–Ω–æ–ø–∫–∏!
        'HTML',
        existing_parent_id,  # –°–æ—Ö—Ä–∞–Ω—è–µ–º parent_id!
        existing_buttons_per_row,  # –°–æ—Ö—Ä–∞–Ω—è–µ–º buttons_per_row!
        pages_json
    )

    if success:
        print(f"‚úÖ Successfully added pages to: {button_id}")
    else:
        print(f"‚ùå Failed to update: {button_id}")

    print("\n‚úÖ Terminology migration completed!")
    print(f"üìÑ {button_id} now has {len(pages)} pages with navigation buttons")
    print("\nüí° Test in bot: Open –¢–µ—Ä–º–∏–Ω–æ–ª–æ–≥–∏–∏ and use ‚óÄÔ∏è ‚ñ∂Ô∏è buttons to navigate")

